CREATE TABLE IF NOT EXISTS users (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  username   TEXT      NOT NULL CHECK (LENGTH(username) > 0),
  password   TEXT      NOT NULL CHECK (LENGTH(password) > 0),
  dt_created TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now'))
);


CREATE TABLE IF NOT EXISTS games (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_table   INTEGER   NOT NULL REFERENCES tables (id),
  fk_player  INTEGER   REFERENCES players (id),
  series     INTEGER   NOT NULL DEFAULT 0,
  sequence   INTEGER   NOT NULL DEFAULT 0,
  status     TEXT      NOT NULL DEFAULT 'new',
  opts       JSON      NOT NULL DEFAULT '{}',
  deck       JSON      NOT NULL DEFAULT '[]',
  hands      JSON      NOT NULL DEFAULT '{}',
  talon      JSON      NOT NULL DEFAULT '[]',
  talon0     JSON      NOT NULL DEFAULT '[]',
  bids       JSON      NOT NULL DEFAULT '[]',
  bid        JSON      NOT NULL DEFAULT '{}',
  tricks     JSON      NOT NULL DEFAULT '[]',
  trick      JSON      NOT NULL DEFAULT '[]',
  moves      JSON      NOT NULL DEFAULT '[]',
  discards   JSON      NOT NULL DEFAULT '[]',
  score      JSON      NOT NULL DEFAULT '{}',
  dt_created TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now'))
);


CREATE TABLE IF NOT EXISTS log (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_user    INTEGER   REFERENCES users (id),
  action     TEXT,
  data       JSON,
  dt_created TIMESTAMP DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now'))
);


CREATE TABLE IF NOT EXISTS online (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_user    INTEGER   NOT NULL REFERENCES users (id),
  fk_table   INTEGER   REFERENCES tables (id),
  active     INTEGER   NOT NULL DEFAULT 1,
  dt_online  TIMESTAMP DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_created TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  UNIQUE (fk_user, fk_table)
);


CREATE TABLE IF NOT EXISTS players (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_table   INTEGER   NOT NULL REFERENCES tables (id),
  fk_game    INTEGER   REFERENCES games (id),
  fk_user    INTEGER   REFERENCES users (id),
  sequence   INTEGER   NOT NULL DEFAULT 0,
  status     TEXT,
  expected   JSON      DEFAULT '{}',
  hand       JSON      NOT NULL DEFAULT '[]',
  hand0      JSON      NOT NULL DEFAULT '[]',
  moves      JSON      NOT NULL DEFAULT '[]',
  tricks     JSON      NOT NULL DEFAULT '[]',
  dt_created TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_deleted TIMESTAMP
);


CREATE TABLE IF NOT EXISTS requests (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_user    INTEGER   NOT NULL REFERENCES users (id),
  fk_table   INTEGER   NOT NULL REFERENCES tables (id),
  fk_game    INTEGER   REFERENCES games (id),
  type       TEXT      NOT NULL,
  status     TEXT      NOT NULL DEFAULT 'new',
  opts       JSON      NOT NULL DEFAULT '{}',
  public     INTEGER   NOT NULL DEFAULT 0,
  dt_created TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now'))
);


CREATE TABLE IF NOT EXISTS table_users (
  id         INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_table   INTEGER   NOT NULL REFERENCES tables (id),
  fk_user    INTEGER   NOT NULL REFERENCES users (id),
  dt_created TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_deleted TIMESTAMP
);


CREATE TABLE IF NOT EXISTS tables (
  id             INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_creator     INTEGER   NOT NULL REFERENCES users (id),
  fk_host        INTEGER   NOT NULL REFERENCES users (id),
  fk_template    INTEGER   NOT NULL REFERENCES templates (id),
  name           TEXT      NOT NULL,
  shortid        TEXT      NOT NULL,
  public         INTEGER   NOT NULL DEFAULT 0,
  opts           JSON      NOT NULL DEFAULT '{}',
  series         INTEGER   NOT NULL DEFAULT 0,
  games          INTEGER   NOT NULL DEFAULT 0,
  players        INTEGER   NOT NULL DEFAULT 0,
  status         TEXT      DEFAULT 'new',
  bids           JSON      NOT NULL DEFAULT '[]',
  scores         JSON      NOT NULL DEFAULT '[]',
  bids_history   JSON      NOT NULL DEFAULT '[]',
  scores_history JSON      NOT NULL DEFAULT '[]',
  dt_created     TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed     TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_deleted     TIMESTAMP
);


CREATE TABLE IF NOT EXISTS templates (
  id          INTEGER   PRIMARY KEY AUTOINCREMENT NOT NULL,
  fk_creator  INTEGER   REFERENCES users (id),
  name        TEXT      NOT NULL CHECK (LENGTH(name) > 0),
  opts        JSON      NOT NULL DEFAULT '{}',
  description TEXT      NOT NULL DEFAULT '',
  i18n        JSON      NOT NULL DEFAULT '{}',
  dt_created  TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_changed  TIMESTAMP NOT NULL DEFAULT (STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now')),
  dt_deleted  TIMESTAMP
);


CREATE TRIGGER IF NOT EXISTS on_update_games AFTER UPDATE ON games
BEGIN
UPDATE games SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


CREATE TRIGGER IF NOT EXISTS on_update_online AFTER UPDATE ON online
BEGIN
UPDATE online SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


CREATE TRIGGER IF NOT EXISTS on_update_players AFTER UPDATE ON players
BEGIN
UPDATE players SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


CREATE TRIGGER IF NOT EXISTS on_update_requests AFTER UPDATE ON requests
BEGIN
UPDATE requests SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


CREATE TRIGGER IF NOT EXISTS on_update_tables AFTER UPDATE ON tables
BEGIN
UPDATE tables SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


CREATE TRIGGER IF NOT EXISTS on_update_templates AFTER UPDATE ON templates
BEGIN
UPDATE templates SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


CREATE TRIGGER IF NOT EXISTS on_update_users AFTER UPDATE ON users
BEGIN
UPDATE users SET dt_changed = STRFTIME('%Y-%m-%d %H:%M:%f000+00:00', 'now') WHERE id = NEW.id;
END;


INSERT OR IGNORE INTO templates (id, name, opts, description, i18n, dt_created, dt_changed) VALUES (1, 'Thousand', '{"sort": ["suite", "level"], "suites": "HSDC", "trump": true, "lead": {"0": "bidder", "*": "trick"}, "talon": {"face": false}, "move": {"win": {"suite": true, "level": true}, "pass": false, "cards": 1, "crawl": 0, "response": {"suite": {"trump": "optional"}}, "special": {"wheels": {"0": false, "*": [["AD", "0D"], ["AH", "0H"], ["AS", "0S"], ["AC", "0C"]], "condition": {"opt": "trump", "suite": true}}, "trump": {"0": false, "*": [["KD", "QD"], ["KH", "QH"], ["KS", "QS"], ["KC", "QC"]], "condition": {"min": 3}}}}, "reveal": true, "hand": 7, "trick": true, "players": [3, 4], "complete": {"score": 1000}, "levels": "9JQK0A", "bidding": {"sell": true, "blind": true, "min": 60, "max": {"blind": 120, "*": 120, "trump": 340}, "distribute": true, "pass_final": true, "talon": true, "step": 5, "pass": true}, "pass": false, "cards": ["9S", "9H", "9D", "9C", "JS", "JH", "JD", "JC", "QS", "QH", "QD", "QC", "KS", "KH", "KD", "KC", "0S", "0H", "0D", "0C", "AS", "AH", "AD", "AC"], "redeal": {"reveal": true, "condition": {"min": 3, "hand": ["9H", "9S", "9D", "9C"]}}, "points": {"trick": {"A": 11, "K": 4, "J": 2, "Q": 3, "0": 10, "9": 0}, "bidonly": {"min": 900}, "bonuses": {"blind": {"value": 2, "op": "mul"}}, "penalties": {"blind": {"value": -2, "op": "mul"}, "nochange": {"op": "add", "value": -100, "times": 3}, "bid": {"value": -1, "op": "mul"}}, "special": {"wheels": 120, "trump": {"C": 100, "H": 60, "S": 80, "D": 40}}}}', X'', '{"et": {"template.name": "Tuhat", "wheels": "rattad", "template.description": "Tihide v\u00f5tmise m\u00e4ng 3-4 m\u00e4ngijale, pakkumise ja v\u00e4ljak\u00e4imisega. M\u00e4ngitakse pakiga, kus on 24 kaarti, \u00fcheksad kuni \u00e4ssad. Eesm\u00e4rgiks on j\u00f5uda esimesena 1000 punktini.\n\nKaasakiskuv m\u00e4ng, populaarne Eestis ja mujal Ida-Euroopas. V\u00f5ib kesta tunde, harva alla tunni.\n\n\n### Pakkumine\n\nKaardid jagatakse m\u00e4ngijatele v\u00e4lja (7 kaarti igale kui 3 m\u00e4ngijat, 6 kaarti kui 4 m\u00e4ngijat), \u00fclej\u00e4\u00e4nud l\u00e4hevad laua keskele, pilt allapoole (3 kaarti kui 3 m\u00e4ngijat, 4 kaarti kui 4 m\u00e4ngijat). M\u00e4ngijad hakkavad ringiratast pakkuma oma kaartide p\u00f5hjal: palju punkte nad v\u00f5idaksid kui nad saavad laual olevad lisakaardid endale. Madalaim pakkumine on 60 punkti, k\u00f5rgeim vaikimisi 120, rohkem saab pakkuda ainult siis kui m\u00e4ngijal on trump k\u00e4es.\n\nPakkuma ei pea, m\u00e4ngija v\u00f5ib passida. Passimine on l\u00f5plik - hiljem vahele pakkuda m\u00e4ngija ei saa.\n\nKui k\u00f5ik m\u00e4ngijad passivad, tehakse uued kaardid.\n\nKui m\u00e4ngijal on k\u00e4es v\u00e4hemalt 3 \u00fcheksat, v\u00f5ib ta oma esimesel pakkumisel n\u00f5uda uusi kaarte.\n\n\n#### Pime\n\nM\u00e4ngija v\u00f5ib teha _pime_ pakkumise, kui nad ei ole oma k\u00e4es olevaid kaarte vaadanud. Kui neil \u00f5nnestub pakutud punktid t\u00e4is saada, v\u00f5idavad nad topelt nii palju. Kui neil ei \u00f5nnestu punkte t\u00e4is saada, kaotavad nad topelt sama palju. Suurim lubatud pimepakkumine on 120 punkti.\n\n\n#### M\u00fc\u00fcmine\n\nKui pakkumise v\u00f5itnud m\u00e4ngija n\u00e4eb lisakaarte ja leiab, et tal ei \u00f5nnestu punkte t\u00e4is saada, v\u00f5ib ta lisakaardid \u00e4ra _m\u00fc\u00fca_. Lisakaardid l\u00e4hevad laua keskele tagasi, seekord pilt \u00fclespoole, ja m\u00e4ngijad saavad j\u00e4tkata pakkumist, alustades m\u00fc\u00fcjale j\u00e4rgmisest m\u00e4ngijast. M\u00e4ngijad, kes esimene kord passisid, v\u00f5ivad pakkumises osaleda. Ka m\u00fc\u00fcja v\u00f5ib pakkumises osaleda, kui keegi tema esimese pakkumise on \u00fcle pakkunud.\n\nKui teine m\u00e4ngija ostab lisakaardid \u00e4ra, ei saa m\u00fc\u00fcja mingit karistust. Kui keegi ei soovi osta, siis m\u00fc\u00fcja lihtsalt j\u00e4tkab m\u00e4ngu, lisakaardid k\u00e4es.\n\nKaarte teist korda m\u00fc\u00fca ei saa.\n\n\n### Jagamine\n\nPakkumise v\u00f5itnud m\u00e4ngija saab lisakaardid endale k\u00e4tte. N\u00fc\u00fcd ta peab andma \u00fche vabalt valitud kaardi igale m\u00e4ngijale, nii et k\u00f5igil on v\u00f5rdselt kaarte k\u00e4es.\n\nP\u00e4ris kaartidega m\u00e4ngimisel peab pakkumise v\u00f5itnud m\u00e4ngija alati meeles pidama, et tal on vaja teistele m\u00e4ngijatele kaarte jagada. Kui tal see meelest l\u00e4heb ja ta n\u00e4iteks \u00e4ssa v\u00e4lja k\u00e4ib kui tavalise esimese k\u00e4igu, pilt \u00fclespoole, siis temast j\u00e4rgmine m\u00e4ngija v\u00f5ib selle \u00e4ssa endale v\u00f5tta kui talle jagatud kaardi.\n\n\n### M\u00e4ngimine\n\nPakkumise v\u00f5itnud m\u00e4ngija alustab ringi ja k\u00e4ib \u00fche kaardi. Iga m\u00e4ngija ringiratast peab k\u00e4ima \u00fche kaardi vastu, sama masti kui neil on, \u00fcksk\u00f5ik mida muud kui pole. Tihi v\u00f5idab m\u00e4ngija, kes k\u00e4is esimeses mastis k\u00f5ige tugevama kaardi, ja ta saab alustada uut ringi.\n\nTeisest mastist kaardi k\u00e4imine kaotab tihi, hoolimata kaardi tugevusest (nt poti \u00e4ss ei v\u00f5ida \u00e4rtu \u00fcheksat). V\u00e4lja arvatud siis kui m\u00e4ngija k\u00e4ib trumpi: trumbimast v\u00f5idab teisi maste.\n\nKaartide tugevus on: \u00e4ss tugevaim, k\u00fcmme, kuningas, emand, poiss, \u00fcheksa n\u00f5rgeim.\n\nM\u00e4ngu ajal saab uuesti vaadata ainult viimase tihi kaarte.\n\n\n#### Roomamine\n\nKui pakkumise v\u00f5itnud m\u00e4ngijal ei ole \u00fchtegi \u00e4ssa, v\u00f5ivad nad oma esimesel k\u00e4igul _roomata_: k\u00e4ivad kaardi v\u00e4lja, pilt allapoole. Teised m\u00e4ngijad k\u00e4ivad oma kaardi samuti pilt allapoole, tihi v\u00f5idab ikkagi see m\u00e4ngija, kes k\u00e4is esimeses mastis k\u00f5ige tugevama kaardi.\n\n\n### Trump\n\nKui ringi alustav m\u00e4ngija on juba \u00fche tihi v\u00f5tnud, neil on v\u00e4hemalt kolm kaarti veel k\u00e4es, ja neil on k\u00e4es \u00fchest mastist kuningas ja emand, saavad nad teha _trumpi_: k\u00e4ivad \u00fche nendest kahest kaardist ja \u00fctlevad \"trump\". Sellest hetkest on see mast trumbimast, teistest mastidest tugevam.\n\n\u00dche m\u00e4ngu jooksul saab teha mitu trumpi, j\u00e4rgmine trump kustutab eelmise.\n\n#### Rattad\n\nLisaks, kui ringi alustaval m\u00e4ngijal on k\u00e4es trumbimasti \u00e4ss ja k\u00fcmme, saavad nad teha _rattad_: k\u00e4ivad \u00fche nendest kahest kaardist ja \u00fctlevad \"rattad\". See annab neile lisa 120 punkti.\n\n\n### Punktid\n\nKui k\u00f5ik kaardid on k\u00e4idud, loetakse punktid kokku. Iga v\u00f5etud tihi annab m\u00e4ngijale punkte tihis saadud kaartide eest:\n\n- \u00e4ss:      11 punkti\n- k\u00fcmme:    10 punkti\n- kuningas:  4 punkti\n- emand:     3 punkti\n- poiss:     2 punkti\n- \u00fcheksa:    0 punkti\n\nK\u00f5ik kaardid kokku annavad 120 punkti.\n\nIga tehtud trump annab m\u00e4ngijale punkte s\u00f5ltuvalt mastist:\n\n- risti:  100 punkti\n- poti:    80 punkti\n- \u00e4rtu:    60 punkti\n- ruutu:   40 punkti\n\nRattad annavad m\u00e4ngijale 120 punkti.\n\nPakkumise v\u00f5itnud m\u00e4ngija saab t\u00e4pselt pakkumises \u00f6eldud punktid, kui nad v\u00e4hemalt nii palju punkte kokku saavad. Lisapunktid \u00fcle pakkumise ei anna neile rohkem midagi. Kui pakkumise v\u00f5itnud m\u00e4ngija ei suuda pakutud punkte kokku saada, kaotavad nad punkte pakutud numbri v\u00f5rra.\n\nPimepakkumine kahekordistab nii v\u00f5idu kui kaotuse.\n\nTeised m\u00e4ngijad saavad punkte nii, nagu nende tihid ja trumbid jne neile annavad.\n\nKui m\u00e4ngija punktid pole kolm m\u00e4ngu j\u00e4rjest muutunud, kaotab m\u00e4ngija 100 punkti. Punktid v\u00f5ivad minna alla nulli, ilma alumise piirita.\n\nKui m\u00e4ngijal on 900 punkti v\u00f5i rohkem, saavad nad punkte juurde teenida ainult pakkumist v\u00f5ites ja pakutud punkte t\u00e4is saades. Teise m\u00e4ngija pakutud m\u00e4ngus ei saa nad \u00fchtegi punkti, hoolimata sellest mitu tihi ja trumpi nad v\u00f5tavad ja teevad."}}', '2020-04-18 22:07:23.000000+00:00', '2020-05-21 18:54:00.166000+00:00');

INSERT OR IGNORE INTO templates (id, name, opts, description, i18n, dt_created, dt_changed) VALUES (2, 'Arschloch', '{"sort": "level", "suites": "HSDCX", "lead": {"0": {"ranking": -1}, "*": "trick"}, "discards": true, "move": {"cards": "*", "win": {"last": true, "level": "all"}, "pass": true, "response": {"amount": true, "level": true}, "level": true}, "hand": 19, "players": [3, 7], "ranking": {"finish": true}, "nextgame": {"distribute": {"ranking": true, "max": 3}}, "cards": ["3S", "3H", "3D", "3C", "4S", "4H", "4D", "4C", "5S", "5H", "5D", "5C", "6S", "6H", "6D", "6C", "7S", "7H", "7D", "7C", "8S", "8H", "8D", "8C", "9S", "9H", "9D", "9C", "0S", "0H", "0D", "0C", "JS", "JH", "JD", "JC", "QS", "QH", "QD", "QC", "KS", "KH", "KD", "KC", "AS", "AH", "AD", "AC", "2S", "2H", "2D", "2C", "XX", "YX", "ZX"], "levels": "34567890JQKA2X", "reveal": false}', X'', '{"et": {"template.description": "\nM\u00e4ng 3-7 m\u00e4ngijale, kus v\u00f5itjad ja kaotajad vahetavad omavahel kaarte j\u00e4rgmistes\nm\u00e4ngudes. M\u00e4ngitakse t\u00e4ispakiga, 55 kaarti, k\u00f5ik alates kahtedest kuni \u00e4ssade\nkuni jokkeriteni. Igas m\u00e4ngus on eesm\u00e4rgiks oma kaartidest k\u00f5ige varem vabaneda;\nesimesena kaartidest lahtisaanu on v\u00f5itja ja viimasena lahtisaanu on kaotaja.\n\nParim m\u00e4ngu kulgemine on 4-5 m\u00e4ngijaga.\n\nLihtsad reeglid, kaasakiskuv ja s\u00f5ltuvusttekitav. M\u00e4ng v\u00f5ib kesta tunde,\naga sobib ka kiireks meelelahutuseks millegi vahepeal.\n\nMaailmas tuntud paljude nimede all: Asshole, Capitalism, President, Scum.\n\n\n### M\u00e4ngimine\n\nK\u00f5ik kaardid jagatakse m\u00e4ngijate vahel v\u00e4lja (paratamatult ei jagu t\u00e4pselt,\nv\u00e4lja arvatud 5 m\u00e4ngija puhul). Esimeses m\u00e4ngus alustab jagajast j\u00e4rgmine,\nj\u00e4rgmistes m\u00e4ngudes alustab eelmise m\u00e4ngu kaotaja.\n\nM\u00e4ngija alustab ringi, k\u00e4ies v\u00e4lja \u00fcksiku v\u00f5i paari v\u00f5i kolmiku v\u00f5i neliku,\nnt 3 \u00fcheksat. J\u00e4rgmised m\u00e4ngijad ringiratast peavad sinna peale k\u00e4ima\nv\u00e4hemalt sama palju kaarte, v\u00e4hemalt sama tugevaid, ka k\u00f5ik \u00fche k\u00f5rgusega.\nKaardimastid ei loe. M\u00e4ngija v\u00f5ib passida; passides see ring rohkem k\u00e4ia ei saa.\n\nKui k\u00f5ik m\u00e4ngijad peale viimasena m\u00e4nginu on passinud, l\u00e4hevad kaardid maha\nja viimane m\u00e4ngija saab alustada j\u00e4rgmist ringi.\n\nKui laual tulevad kokku \u00fche k\u00f5rguse k\u00f5ik kaardid (neli tavakaarti v\u00f5i kolm jokkerit),\nl\u00e4hevad kaardid maha ja viimane m\u00e4ngija saab alustada j\u00e4rgmist ringi.\n\nKaartide tugevus on: jokker tugevaim, kaks, \u00e4ss, kuningas, emand, poiss, k\u00fcmme,\n\u00fcheksa, .., neli, kolm n\u00f5rgeim.\n\nM\u00e4ngu ajal saab uuesti vaadata ainult viimasena mahal\u00e4inud ringi kaarte.\n\n\n### Vahetamine\n\nJ\u00e4rgmistes m\u00e4ngudes vahetavad eelmise m\u00e4ngu v\u00f5itjad ja kaotajad omavahel kaarte,\nkaotajad peavad \u00e4ra andma oma k\u00f5ige tugevamad kaardid ja v\u00f5itjad saavad \u00e4ra\nanda vabalt valitud kaarte. Esimene ja viimane m\u00e4ngija vahetavad 3 kaarti,\nj\u00e4rgmised kohad vahetavad sammu v\u00f5rra v\u00e4hem. Seega v\u00e4hemalt 4 m\u00e4ngija puhul vahetavad\neest ja tagant teine omavahel 2 kaarti, v\u00e4hemalt 6 m\u00e4ngija puhul vahetavad\neest ja tagant kolmas omavahel 1 kaardi.\n\nKui on paaritu arv m\u00e4ngijaid, siis pingereas t\u00e4pselt keskmine ei vaheta kellegagi.\n\nP\u00e4ris kaartidega m\u00e4ngides peab kaotaja uued kaardid tegema.\n\n\n### Kommentaar\n\nVahetamise t\u00f5ttu kipub pingerea ladvik v\u00f5itma ka j\u00e4rgmistes m\u00e4ngudes, sest nad\nmitte ainult ei saa oma n\u00f5rkasid kaarte \u00e4ra anda, nad saavad ka keskenduda \u00fcksikute\nkaartide \u00e4raandmisele - ja lisaks nad saavad veel h\u00e4id kaarte vastu. Tihtipeale\nm\u00e4ngu alguses s\u00f5divad esimene-teine koht omavahel kuni kaartidest lahti saavad,\nkuna kaotanud m\u00e4ngijatel lihtsalt pole nii k\u00f5rgeid kaarte ja nii palju mitmikuid k\u00e4ia.\n\nViimasel kohal oleva m\u00e4ngija ainsad eelised on: ta saab alustada esimest ringi,\nja s\u00f5ltuvalt m\u00e4ngijate arvust jagub talle enamasti kaarte \u00fche v\u00f5rra v\u00e4hem.\n\nKes t\u00e4pselt on siin m\u00e4ngus Arschloch - esimene v\u00f5i viimane koht - on vaieldav."}}', '2020-05-03 19:46:23.843000+00:00', '2020-05-08 21:10:13.930000+00:00');

INSERT OR IGNORE INTO templates (id, name, opts, description, i18n, dt_created, dt_changed) VALUES (3, 'Five Sheets', '{"sort": ["suite", "level"], "suites": "HSDC", "trump": true, "ranking": {"finish": true}, "talon": {"trump": true, "lead": true, "face": false}, "move": {"cards": 1, "response": {"suite": {"trump": "alternative"}, "level": true, "follow": {"cards": 1}}, "retreat": {"uptake": {"stack": 5}}}, "hand": 5, "players": [2, 8], "levels": "2345678909JQKA", "cards": ["2H", "2S", "2D", "2C", "3H", "3S", "3D", "3C", "4H", "4S", "4D", "4C", "5H", "5S", "5D", "5C", "6H", "6S", "6D", "6C", "7H", "7S", "7D", "7C", "8H", "8S", "8D", "8C", "9H", "9S", "9D", "9C", "0H", "0S", "0D", "0C", "JH", "JS", "JD", "JC", "QH", "QS", "QD", "QC", "KH", "KS", "KD", "KC", "AH", "AS", "AD", "AC"], "refill": true, "stack": true}', X'', '{"et": {"template.name": "Viis lehte", "follow": "peale", "Must follow": "Peab k\u00e4ima j\u00e4rgmise kaardi", "No need to follow": "Ei pea k\u00e4ima j\u00e4rgmist kaarti", "template.description": "Lihtne m\u00e4ng 2-8 m\u00e4ngijale, kus m\u00e4ngijad peavad tapma eelmise kaardi ja k\u00e4ima kaardi j\u00e4rgmisele m\u00e4ngijale tapmiseks. M\u00e4ngitakse pakiga, kus on 52 kaarti, kahed kuni \u00e4ssad. Eesm\u00e4rgiks on oma kaartidest k\u00f5ige varem vabaneda; esimesena kaartidest lahtisaanu on v\u00f5itja ja viimasena lahtisaanu on kaotaja.\n\n\n### M\u00e4ngimine\n\nIgale m\u00e4ngijale jagatakse 5 kaarti, \u00fclej\u00e4\u00e4nud pakk l\u00e4heb laua keskele, pilt allapoole, \u00fcks kaart pannakse paki alla trumbiks, pilt \u00fclespoole, ja \u00fcks kaart k\u00e4iakse v\u00e4lja laua keskele virna alguskaardina.\n\nJagajast j\u00e4rgmine alustab, k\u00e4ies \u00fche kaardi mis tapab laua viimase kaardi, ja veel \u00fche kaardi j\u00e4rgmisele m\u00e4ngijale tapmiseks. Kaardi tapmiseks tuleb k\u00e4ia tugevam kaart samas mastis, v\u00f5i trumpkaart. Trumpi saab m\u00e4ngida ka siis kui m\u00e4ngijal on k\u00e4ia samas mastis tugevamat.\n\nP\u00e4rast k\u00e4imist v\u00f5tab m\u00e4ngija oma kaardid laua pakist j\u00e4lle t\u00e4is, et neil oleks 5 kaarti k\u00e4es. Paki all olev trumpkaart v\u00f5etakse viimasena.\n\nKui m\u00e4ngija ei suuda v\u00f5i ei soovi laua viimast kaarti tappa, peab ta laua virnast kuni 5 kaarti \u00fcles v\u00f5tma. Kui laua virn t\u00fchjaks saab, k\u00e4iakse pakist \u00fcks kaart v\u00e4lja virna. Kui nii laua virn kui pakk t\u00fchjaks saavad, peab j\u00e4rgmine m\u00e4ngija k\u00e4ima ainult \u00fche kaardi.\n\nKui m\u00e4ngijal on ainult \u00fcks kaart k\u00e4es, ei pea nad teist kaarti k\u00e4ima.\n\nKaartide tugevus on: \u00e4ss tugevaim, kuningas, emand, poiss, k\u00fcmme, .., kolm, kaks n\u00f5rgeim."}}', '2020-05-20 09:49:39.140000+00:00', '2020-05-21 18:54:00.256000+00:00');
